function applyCurrencyFormatting(){document.querySelectorAll("input[data-currency]").forEach((t=>{if("true"===t.dataset.currencyBound)return;t.dataset.currencyBound="true";const e=t.dataset.locale||"pt-BR",a=t.dataset.currency||"BRL",r=new Intl.NumberFormat(e,{style:"currency",currency:a}),c=t=>{const e=t.value.replace(/\D/g,"");t.value=e?r.format(Number(e)/100):""};t.addEventListener("input",(t=>c(t.target))),t.addEventListener("paste",(t=>{t.preventDefault();const e=(t.clipboardData||window.clipboardData).getData("text");t.target.value=e,c(t.target)}))}))}document.querySelectorAll("input[data-currency]").forEach((t=>{const e=t.dataset.locale||"pt-BR",a=t.dataset.currency||"BRL",r=new Intl.NumberFormat(e,{style:"currency",currency:a}),c=t=>{const e=t.value.replace(/\D/g,"");t.value=e?r.format(Number(e)/100):""};t.addEventListener("input",(t=>c(t.target))),t.addEventListener("paste",(t=>{t.preventDefault();const e=(t.clipboardData||window.clipboardData).getData("text");t.target.value=e,c(t.target)}))}));